{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyMXVb7X0Yi2RSCR2BhFglQH"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"Wd2Va7KKZba6"},"outputs":[],"source":["# Step 1: Save this as app.py later\n","app_code = '''\n","import streamlit as st\n","from textblob import TextBlob\n","import pandas as pd\n","import datetime\n","\n","st.set_page_config(page_title=\"MoodDo\", layout=\"centered\")\n","\n","st.title(\"ðŸ˜„ MoodDo - Mood-Based Task Organizer\")\n","\n","# Load previous data\n","try:\n","    df = pd.read_csv(\"mood_log.csv\")\n","except:\n","    df = pd.DataFrame(columns=[\"Date\", \"Mood\", \"Task\"])\n","\n","# Input mood\n","mood = st.text_input(\"How are you feeling today?\")\n","task = st.text_input(\"What task do you plan to do?\")\n","\n","if st.button(\"Add Entry\"):\n","    if mood and task:\n","        blob = TextBlob(mood)\n","        sentiment = \"Positive\" if blob.sentiment.polarity >= 0 else \"Negative\"\n","        today = datetime.date.today().strftime(\"%Y-%m-%d\")\n","        new_row = {\"Date\": today, \"Mood\": sentiment, \"Task\": task}\n","        df = pd.concat([df, pd.DataFrame([new_row])], ignore_index=True)\n","        df.to_csv(\"mood_log.csv\", index=False)\n","        st.success(f\"Task added with mood: {sentiment}\")\n","    else:\n","        st.warning(\"Please fill both fields.\")\n","\n","# Display task list\n","if not df.empty:\n","    st.subheader(\"ðŸ“‹ Mood-Task History\")\n","    st.dataframe(df.tail(10))\n","'''\n","\n","# Save to file\n","with open(\"app.py\", \"w\") as f:\n","    f.write(app_code)\n"]}]}